import Link from "next/link";

const Nav = () => (
  
  <nav>  
      <input type="checkbox"  id="check"/>
    <label for="check" className="checkbtn"></label>
    <i className="fas fa-bars"></i>
    <Link href="/">
          <a className="logo"> Home</a>
        </Link>
    
    <ul>
      <li>
        <Link href="/whoweare">
          <a className="active"> Who we are</a>
        </Link>
      </li>
      <li>
        <Link href="/services">
          <a> Business and Services </a>
        </Link>
      </li>
      <li>
        <Link href="/rigs">
          <a> Rigs</a>
        </Link>
      </li>
      <li>
        <Link href="/innovations">
          <a> A well of Innovation</a>
        </Link>
      </li>
      <li>
        <Link href="/careers">
          <a> Careers</a>
        </Link>
      </li>
      <li>
        <Link href="/investors">
          <a> Investors</a>
        </Link>
      </li>
      <li>
        <Link href="/media">
          <a> Media</a>
        </Link>
      </li>
      <li>
        <Link href="/contact">
          <a> Contact Us</a>
        </Link>
      </li>
    </ul>
    
    <style jsx>{`
      nav{
        background-color: rgb(48, 48, 48);
        height: 80px;
        width: 100%;
      }
      a.logo{
        color: white;
        font-size: 35px;
        line-height: 80px;
        padding: 0 10px;
        font-weight: bold;
      }

      nav ul{
        float: right;
        margin-right: 20px;
      }
      nav ul li{
        display: inline-block;
      line-height: 80px;
      margin: 0 5px;
      }
      nav ul li a{
        color: white;
        font-size: 17px;
        padding: 7px 13px;
        border-radius: 3px;
      }

      a.active, a:hover{
        background-color: red;
        transition: .5s;
      }
      .checkbtn{
        font-size: 30px;
        color: red;
        float: right;
      }

    `}</style>
  </nav>
);

export default Nav;
