import React from 'react';
import Link from 'next/link';
import Layout from './components/AdminLayout';

class NewsArticle extends React.Component{
  constructor(props){
    super(props);
    this.state ={
title: "",
body:""
    };
  }


  handleChange = (e) =>{
this.setState({
 [e.target.name]: e.target.value
})
  }

  handleSubmit = (e)=>{
    console.log(this.state);
  }

  render(){
          const{title, body} = this.state;

    return(
      <Layout> 
            <h1>Create News</h1>
        
          <div className="news-form">
            <form>
              <label for="title">Article Title</label><br/>
              <input type="text" id="title" name="title" value={title} required onChange = {this.handleChange()}/><br/>
              <label for="article">Article Body</label><br/>
              <textarea type="text" id="body" name="body" value ={body} required onChange = {this.handleChange()}/><br/>
              <button onChange ={this.handleSubmit()}>Submit</button>
            </form>
         
          </div>
 
      
        <style jsx>
            {`
            
    
            .news-form{
              box-shadow: 1px 1px 1px 1px #888888;
              border-radius: 10px;
              padding: 5px;
              width: 40%;
              min-height: 70vh;
              margin: auto;
                          }
    h1{
      text-align: center;
      margin: auto;
      padding-top: 2em;
    }
           
      .news-form form{
        margin: 3em;
       
      }

      input{
         width: 90%;
         height:2em;
         border-radius: 5px;
         margin-bottom: 1em;
      }
      textarea{
        width: 90%;
        border-radius: 4px;
        min-height: 13em;
      }

      button{
        background-color: #0000fe;
        border-radius: 6px;
        width: 7em;
        height: 2em;
        color: white;
      }
            `}
        </style>
      </Layout>
      
          );
  }
}
export default NewsArticle;
