import React from 'react';
import Link from 'next/link';
import Layout from './components/AdminLayout';

class NewsArticle extends React.Component{
  constructor(props){
    super(props);
    this.state ={

    };
  }

  render(){
    return(
      <Layout> 
            <h1>Create News</h1>
        
          <div className="news-form">
            <form>
              <label for="title">Article Title</label><br/>
              <input type="text" id="title" name="title" required/><br/>
              <label for="article">Article Body</label><br/>
              <textarea type="text" id="body" name="body"  required/><br/>
              <button>Submit</button>
            </form>
         
          </div>
 
      
        <style jsx>
            {`
            
    
            .news-form{
              box-shadow: 1px 1px 1px 1px #888888;
              border-radius: 10px;
              padding: 5px;
              width: 40%;
              min-height: 70vh;
              margin: auto;
                          }
    h1{
      text-align: center;
      margin: auto;
      padding-top: 2em;
    }
           
      .news-form form{
        margin: 3em;
       
      }

      input{
         width: 90%;
         height:2em;
         border-radius: 4px;
         margin-bottom: 1em;
      }
      textarea{
        width: 90%;
        border-radius: 4px;
        min-height: 10em;
      }

      button{
        background-color: #0000ff;
        border-radius: 4px;
        width: 6em;
        height: 1.5em;
      }
            `}
        </style>
      </Layout>
      
          );
  }
}
export default NewsArticle;
