import Layout from './components/MyLayout';

export default function Investors() {
  return (
    <Layout>
      <div className="investors">
      <h5>Home > The Managament Team</h5>
        <hr/>
<br/>
<br />
<article>
  <section>
    <h2>The Managment Team</h2>

        </section>
        <section>
         <div className="product-card">
            <img src="/petrov.jpeg" alt="Alex Petrov" />
            <h4><a href="">Alex Petrov</a></h4>
            <p>Chief Executive Officer</p>
        </div>
        <div className="product-card">
            <img src="/gale.jpeg" alt="image 1" />
            <h4><a href="">Gearge Gale</a></h4>
            <p >Use this for group head of HSE and Operational Integrity</p> 
        </div>
        <div className="product-card">
            <img src="https://dummyimage.com/300x300/7EC0EE/000&text=Product+Image+1" alt="image 1" />
            <h4><a href="">Video title 1</a></h4>
            <p >RRP: £18.99</p>
        </div>
        <div className="product-card">
            <img src="https://dummyimage.com/300x300/7EC0EE/000&text=Product+Image+1" alt="image 1" />
            <h4><a href="">Video title 1</a></h4>
            <p >RRP: £18.99</p>
         
        </div>
  </section>
</article>

      </div>
      <style jsx>{`
.investors{
    margin: 5em;
}
.investors img{
  width: 100%;
}
    
.investors p{
  font-size: 1em;
  margin: 1em 0 1em 0;
}
.investors h1{
  margin: 1em 0 0.2em 0;

}

.investors h2{
  color:grey;
}

.product-card{
              display: inline-block;
              box-sizing: border-box;
              width: 22em;
              margin: 20px;
              background-color: gray;
              color: white;
              height: 30em;
          }

          .product-card:hover{
              background-color: red;
          }
          .product-card img{
              width: 100%;
              max-height: 70%;
              border: 8px solid lightgrey;
          }
          .product-card h4 {
    margin: 30px 0 15px;
  }
  .product-card a {
    color: white;
    text-decoration: none;
    padding-left: 8px;
    padding-right:8px;
  }
  .product-card p {
    line-height: 1.5;
    padding-left: 8px;
    padding-right:8px;
  }
 

@media only screen and (max-width: 800px){
  .investors{
    margin:1em;
    padding-top:30px;
  }
}
   
    `}</style>
    </Layout>
  );
}
