import Layout from './components/MyLayout';
import Link from 'next/link';

export default function Innovations() {
  return (
    <Layout>
      <div className="innovations">
      <h5>Home > The PetroGas Way</h5>
        <hr/>
        <h1> Team PetroGas Way</h1>
        <img src="/innovations.jpg" alt="The petroGas Way"/>
        <p><b>At PetroGas Group, we believe in operating the PetroGas Group Way. This defines the type of company that we are and will continue to be for our people, our clients, the environment and the communities in which we operate. Itâ€™s a global approach that every person believes in and it is our responsibility to work the PetroGas Group Way. </b></p>
<hr />

  <section>
<div>
  <p>The KCA Deutag Way is the combination of worldwide standards, tried and tested policies and procedures, pragmatic work guidelines and core behaviours that help us live and work by our values. It governs how we behave as colleagues and as a company.  It ensures we operate safely, succeed globally and our customers enjoy a world class service.

</p>
<p>The KCA Deutag Way is underpinned by our six core values.</p>
</div>

<div>
  <p></p>
</div>
  </section>


      </div>
      <style jsx>{`
.innovations{
    margin: 5em;
}
.innovations img{
  width: 100%;
}
.innovations p{
  font-size: 1.2em;
  line-height: 1.5em;
  margin: 1em 0 1em 0;
}
.innovations h1{
  margin: 1em 0 0.2em 0;

}

.innovations h2{
  color:grey;
}

.innovations section{
  display: grid;
  grid-template-columns: auto auto;
  grid-column-gap: 3em;
}

@media only screen and (max-width: 800px){
  .innovations{
    margin:1em;
    padding-top:30px;
  }
}
   
    `}</style>
    </Layout>
  );
}
