const Header = () => (
  <header className="header">
    <div className="logo">
      <img src="/petro-logo.jpg" alt="Logo"></img>
  <input type="checkbox"  id="check"/>
  <label for="check"><i className="fas fa-bars"></i>
  </label>
    
    </div>

    <style jsx>
      {`
        header {
          height: 90px;
          width: 100%;
          margin: auto;
          text-align: center;
        }
        .logo {
          height: 100%;
          z-index: 1;
          margin: auto;
          text-align: center;
        }
      
        .logo img {
          width: 150px;
          height: 80px;
        }
        @media only screen and (max-width: 800px){
          header{
            display: block;
            width: 100%;
            height: 40px;
            margin-left: 20px; 
          }
          .logo{
            width: 100%;
          }
          .logo img{
            float: left;
            width: 80px;
            height:30px;
          }
          .logo label i{
            display: relative;
            float: right;
            color: black;
          }
        }
        @media only screen and (min-width: 800px){
          .logo input{
          display: none;
        }
        .logo label{ 
          display: none;

        }
        }
      `}
    </style>
  </header>
);

export default Header;
